mapper.select:
SELECT p.*, c.name as category_name FROM product p LEFT JOIN category c ON p.category_id = c.id WHERE p.deleted = 0
AND p.category_id = #{categoryId}
AND (p.name LIKE CONCAT('%', #{keyword}, '%') OR p.subtitle LIKE CONCAT('%', #{keyword}, '%') OR p.description LIKE CONCAT('%', #{keyword}, '%'))
AND p.status = #{status}
ORDER BY p.create_time DESC

SELECT p.*, c.name as category_name, MATCH(p.name, p.subtitle) AGAINST(#{keyword} IN BOOLEAN MODE) as relevance FROM product p LEFT JOIN category c ON p.category_id = c.id WHERE p.deleted = 0
AND p.category_id = #{categoryId}
AND MATCH(p.name, p.subtitle) AGAINST(#{keyword} IN BOOLEAN MODE)
AND p.status = #{status}
ORDER BY relevance DESC, p.create_time DESC

SELECT * FROM product
WHERE deleted = 0 AND status = 1
AND category_id = #{categoryId}
AND id != #{productId}
ORDER BY sales DESC
LIMIT #{limit}

mapper.update:
UPDATE product
SET stock = stock - #{quantity}
WHERE id = #{productId} AND stock >= #{quantity}

UPDATE product
SET stock = stock + #{quantity}
WHERE id = #{productId}
